import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { HomeScreen } from "../Screens/HomeScreen";
import { ConfigScreen } from "../Screens/ConfigScreen";
import { styles } from "../Configuracoes/styles";
import { BiblioScreen } from "../Screens/BiblioScreen";
import { createDrawerNavigator } from "@react-navigation/drawer";

const Stack = createNativeStackNavigator();
export const RootNavigation = () => {
  return (

    <Stack.Navigator initialRouteName="Drawer">
      <Stack.Screen name="Home" component={HomeScreen} />
      <Stack.Screen
        style={styles.navConfig}
        options={{ headerShown: false }}
        name="Config"
        component={ConfigScreen}
      />
      <Stack.Screen
        style={styles.navConfig}
        options={{ headerShown: false }}
        name="Biblio"
        component={BiblioScreen}
      />
      <Stack.Screen
        name={"Drawer"}
        component={MenuDrawer}
        options={{
          title: "Auth Box",
          headerStyle: {
            backgroundColor: "#4B088A",
          },
          headerTintColor: "#fff",
          headerTitleStyle: {
            fontWeight: "900",
          },
        }}
      />
    </Stack.Navigator>
  );
};
const Drawer = createDrawerNavigator();

function MenuDrawer() {
  return (

    <Drawer.Navigator
      useLegacyImplementation
      initialRouteName="Home"
      screenOptions={{
        drawerType: "front",
        drawerStyle: { width: "60%", color: 'red' },
        overlayColor: "transparent",
      }}
      drawerStyle={{
        backgroundColor: "gray",
      }}
    >


      <Drawer.Screen name="Biblioteca" component={BiblioScreen} />
      <Drawer.Screen name="Configurações" component={ConfigScreen} />
    </Drawer.Navigator>
  );
}
