import * as React from "react";
import { View, Image } from "react-native";
import { Appbar, Modal, Text, Provider, Portal,Button } from "react-native-paper";
import { colors, locations, styles } from "../Configuracoes/styles";
import { LinearGradient } from "expo-linear-gradient";
import { Pressable } from "react-native";

export const BiblioScreen = ({ navigation }) => {




  return (
    <View style={styles.containerBiblio}>

      <LinearGradient // Background Linear Gradient
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 0 }}
        colors={colors}
        locations={locations}
        style={{ height: 7, width: "100%" }}
      />
      <View style={styles.shadow} />
      <Text style={styles.nomeUsuariob}>Bem vindo(a) Helene</Text>






























        <Button style={styles.buttonCL} >
        <Text style={styles.textBL} >+ Criar novo Livro</Text>
        </Button>


      <View
        style={{
          flex: "1",
          flexWrap: "wrap",
          display: "flex",
          gap: "40px",
          marginLeft: "30px",
          flexDirection: "row",
          marginTop:"50px"
        }}
      >
        <View>
          <Image
            source={require("../Images/Livro1.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro1
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
        <View>
          <Image
            source={require("../Images/Livro2.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro2
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
        <View>
          <Image
            source={require("../Images/Livro3.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro3
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
      </View>

      <LinearGradient // Background Linear Gradient
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 0 }}
        colors={colors}
        locations={locations}
        style={{ height: 7, width: "100%", marginTop: "135%" }}
      />
    </View>
  );
};
