import * as React from "react";
import { View, Image,ImageBackground, TouchableOpacity } from "react-native";
import {
  Appbar,
  Modal,
  Text,
  Provider,
  Portal,
  Button,
  TextInput,
} from "react-native-paper";
import { colors, locations, styles } from "../Configuracoes/styles";
import { LinearGradient } from "expo-linear-gradient";


export const BiblioScreen = ({ navigation }) => {
  const MyComponent = () => {
    const [visible, setVisible] = React.useState(false);

    const showModal = () => setVisible(true);
    const hideModal = () => setVisible(false);
    const [text, setText] = React.useState("");

    const containerStyle = {
      backgroundColor: "white",
      height: "380px",
      borderRadius: "25px",
      width: "220px",
    };

  };




  return (
    <View style={styles.containerBiblio}>
      <Appbar.Header style={styles.navConfig}>
        <Appbar.BackAction onPress={_goBack} />
        <Appbar.Content
          titleStyle={{ textAlign: "center", fontWeight: "bold" }}
          title="Biblioteca"
        />
        <Appbar.Action icon="menu" onPress={_handleMore} />
      </Appbar.Header>
      <LinearGradient // Background Linear Gradient
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 0 }}
        colors={colors}
        locations={locations}
        style={{ height: 7, width: "100%" }}
      />
      <View style={styles.shadow} />
      <Text style={styles.nomeUsuariob}>Bem vindo(a) Helene</Text>

      <Provider>
        <Portal>
          <Modal
            style={styles.modalBiblio1}
            visible={visible}
            onDismiss={hideModal}
            contentContainerStyle={containerStyle}
          >
            <View>
              <View style={styles.TituloFecharModal}>
                <Text style={styles.CriarLivros}>Criar Livros</Text>
                <TouchableOpacity onPress={hideModal}>
                  <Image
                    source={require("../Images/FecharModal.png")}
                    style={styles.FecharModal}
                  />
                </TouchableOpacity>
              </View>
              <View style={{ alignItems: "center" }}>
                <ImageBackground
                  source={require("../Images/CriarLivros.png")}
                  style={styles.LivroColorido}
                >
                  <Button>
                    <Image
                      style={styles.MaisDoLivro}
                      source={require("../Images/botaoModal.png")}
                    />
                  </Button>
                </ImageBackground>
              </View>
              <Text style={styles.TituloTitulo}>Título</Text>
              <TextInput
                style={styles.NomeDoLivro}
                label="Nome do livro"
                value={text}
                onChangeText={(text) => setText(text)}
              />
              <Button style={styles.ButtonSalvarBiblio}>
                <Text style={styles.TextButtonSalvarBiblio}>Salvar</Text>
              </Button>
            </View>
          </Modal>
        </Portal>
        <Button style={styles.buttonCL} onPress={showModal}>
          <Text style={styles.textBL}>+ Criar novo Livro</Text>
        </Button>
      </Provider>

      <View
        style={{
          flex: "1",
          flexWrap: "wrap",
          display: "flex",
          gap: "40px",
          marginLeft: "30px",
          flexDirection: "row",
          marginTop: "50px",
        }}
      >
        <View>
          <Image
            source={require("../Images/Livro1.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro1
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
        <View>
          <Image
            source={require("../Images/Livro2.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro2
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
        <View>
          <Image
            source={require("../Images/Livro3.png")}
            style={styles.LivroB}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginLeft: "30px",
              marginTop: "10px",
            }}
          >
            <Text
              style={{
                fontWeight: "bold",
                fontSize: "18px",
                marginLeft: "5px",
                textAlign: "auto",
              }}
            >
              Livro3
            </Text>
            <Image
              style={{
                width: "20px",
                height: "20px",
                marginLeft: "20px",
                position: "relative",
              }}
              source={require("../Images/Vector.png")}
            />
          </View>
        </View>
      </View>

      <LinearGradient // Background Linear Gradient
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 0 }}
        colors={colors}
        locations={locations}
        style={{ height: 7, width: "100%", marginTop: "135%" }}
      />
    </View>
  );
};

export default MyComponent;
