import * as React from "react";
import { View, Image } from "react-native";
import { Modal, Portal, Text, Button, Provider } from 'react-native-paper';

const MyComponent = () => {
  const [visible, setVisible] = React.useState(false);

  const showModal = () => setVisible(true);
  const hideModal = () => setVisible(false);
  const containerStyle = {backgroundColor: 'white', padding: 20,  height:'400px', borderRadius:'50px'};

  return (
    <Provider>
      <Portal>
        <Modal style={{width:"250px",marginLeft:"60px"}} visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
          <View style={{margin:0}}>
          <Text style={{ fontWeight: "bold", fontSize:"20px",
         }}>Criar Livros</Text>
          <Button >
          <Image
              style={{
                width: "150px",
                height: "180px",
              }}
              source={require("../Images/LivroModal.png")}
            />
           </Button>
          </View>
        </Modal>
      </Portal>
      <Button style={{marginTop: 30}} onPress={showModal}>
        Show
      </Button>
    </Provider>
  );
};

export default MyComponent;
