import * as React from "react";
import { View, Image, ImageBackground, TouchableOpacity } from "react-native";

import {
  Modal,
  Portal,
  Text,
  Button,
  Provider,
  TextInput,
} from "react-native-paper";
import { styles } from "../Configuracoes/styles";

const MyComponent = ({navigation}) => {
  const [visible, setVisible] = React.useState(false);

  const showModal = () => setVisible(true);
  const hideModal = () => setVisible(false);
  const [text, setText] = React.useState("");

  const containerStyle = {
    backgroundColor: "white",
    height: "380px",
    borderRadius: "25px",
    width: "220px",
  };

  return (
    <Provider>
      <Portal>
        <Modal
          style={styles.modalBiblio1}
          visible={visible}
          onDismiss={hideModal}
          contentContainerStyle={containerStyle}
        >
          <View>
            <View
              style={styles.TituloFecharModal}
            >
              <Text
                style={styles.CriarLivros}
              >
                Criar Livros
              </Text>
              <TouchableOpacity
                onPress={hideModal}
              >
              <Image
                source={require("../Images/FecharModal.png")}
                style={styles.FecharModal}

              />
              </TouchableOpacity>
            </View>
            <View style={{ alignItems: "center" }}>
              <ImageBackground
                source={require("../Images/CriarLivros.png")}
                style={styles.LivroColorido}
              >
                <Button>

                  <Image
                    style={styles.MaisDoLivro}
                    source={require("../Images/botaoModal.png")}
                  />
                </Button>
              </ImageBackground>
            </View>
            <Text
              style={styles.TituloTitulo}
            >
              TÃ­tulo
            </Text>
            <TextInput
              style={styles.NomeDoLivro}
              label="Nome do livro"
              value={text}
              onChangeText={(text) => setText(text)}
            />
            <Button
              style={styles.ButtonSalvarBiblio}
            >
              <Text
                style={{
                  fontWeight: "bold",
                  fontSize: "15px",
                }}
              >
                Salvar
              </Text>
            </Button>
          </View>
        </Modal>
      </Portal>
      <Button style={{ marginTop: 30 }} onPress={showModal}>
        Show
      </Button>
    </Provider>
  );
};

export default MyComponent;
