import { HomeScreen } from "../Screens/HomeScreen";
import { ConfigScreen } from "../Screens/ConfigScreen";
import { styles } from "../Configuracoes/styles";
import { BiblioScreen } from "../Screens/BiblioScreen";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { Ionicons } from '@expo/vector-icons';

const Stack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();

function MenuDrawer({ navigation }) {
  return (
    <Drawer.Navigator
      initialRouteName="Home"
      screenOptions={{
        drawerType: "slide",
        drawerStyle: { width: "60%" },
        overlayColor: "transparent",
        drawerActiveTintColor: "#4B088A",
        drawerInactiveTintColor: "gray",
        drawerIcon: ({ focused, color, size }) => (
          <Ionicons name={focused ? 'menu' : 'menu-outline'} size={size} color={color} />
        ),
      }}
      drawerContentOptions={{
        activeBackgroundColor: "lightgray",
      }}
    >
      <Drawer.Screen name="Biblioteca" component={BiblioScreen} />
      <Drawer.Screen name="Home" component={HomeScreen} />
      <Drawer.Screen name="Configurações" component={ConfigScreen} />
    </Drawer.Navigator>
  );
}

export const RootNavigation = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Drawer">
        <Stack.Screen
          name="Config"
          component={ConfigScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Biblio"
          component={BiblioScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Drawer"
          component={MenuDrawer}
          options={{
            title: "Auth Box",
            headerStyle: {
              backgroundColor: "#4B088A",
            },
            headerTintColor: "#fff",
            headerTitleStyle: {
              fontWeight: "900",
            },
          }}
        />
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{
            title: "Home",
            headerStyle: {
              backgroundColor: "#4B088A",
            },
            headerTintColor: "#fff",
            headerTitleStyle: {
              fontWeight: "900",
            },
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};
